'use strict';
 
// require thư viện tương tác với mongo
 
var mongoose = require('mongoose');
 
// tạo cấu trúc bảng posts dùng hàm Schema&amp;nbsp;
 
var PostSchema = mongoose.Schema({
    chapter : [{name : String, id: String, slug: String}],
    slug : String,
    status : String,
    genres : [String],
    name : String,
    author: [String],
    summary : String,
    lastChap : {name: String, id: String, date: Number},
    thumb: String,
    hot: Boolean

},{collection: 'truyen'});
 
// export vào app với tên model là Post
 
module.exports = mongoose.model('Post', PostSchema);